/**
 * A transcription element, i.e. a single phrase spoken by either Dasha or its partner.
 */
export interface Transcription {
    /**
     * Who was the speaker of that phrase.
     *
     * `"ai"` is for the Dasha itself.
     *
     * `"human"` is for its conversational partner.
     */
    speaker: "human" | "ai";
    /**
     * The text of the phrase.
     */
    text: string;
    /**
     * A moment in time when the phrase started.
    */
    startTime: Date;
    /**
     * A moment in time when the phrase ended.
    */
    endTime: Date;
}
