/**
 * Global Dasha SDK configuration.
 *
 * @see {@link configure}
 */
export interface Configuration {
    /**
     * Whether to enable error reports being sent to Dasha.
     *
     * @default false
     */
    enableErrorReports?: boolean;
}
/**
 * Set the global {@link Configuration} of the Dasha SDK.
*/
export declare function configure(config: Configuration): void;
